import React, { useState, useEffect } from "react";
import { styled } from "@mui/material/styles";
import { AppBar, Toolbar, Typography, Avatar, Drawer, List, ListItem, ListItemIcon, ListItemText, Container, Grid, Card, CardContent, CardMedia, Button, CircularProgress, Box, CssBaseline } from "@mui/material";
import { Image, Home, UploadFile, Delete } from "@mui/icons-material";
import { DropzoneArea } from "material-ui-dropzone";
import axios from "axios";
import logo from "./logo.png";
import bgImage from "./plant.jpeg";
import { BrowserRouter as Router, Route, Routes, useNavigate } from "react-router-dom";

const drawerWidth = 260;

const Main = styled("main", { shouldForwardProp: (prop) => prop !== "open" })(
  ({ theme, open }) => ({
    marginLeft: open ? drawerWidth : 0,
    transition: theme.transitions.create("margin", {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    flexGrow: 1, // Ensure this takes full width
    padding: 20,
  })
);

export default function ImageUpload() {
  const [file, setFile] = useState(null);
  const [preview, setPreview] = useState(null);
  const [prediction, setPrediction] = useState(null);
  const [loading, setLoading] = useState(false);
  const [open, setOpen] = useState(true);
  const navigate = useNavigate(); // React Router's navigate function

  useEffect(() => {
    if (!file) return;
    const objectUrl = URL.createObjectURL(file);
    setPreview(objectUrl);
    return () => URL.revokeObjectURL(objectUrl);
  }, [file]);

  const handleFileUpload = (files) => {
    if (files.length > 0) {
      setFile(files[0]);
      setPrediction(null);
    }
  };

  const uploadImage = async () => {
    setLoading(true);
    const formData = new FormData();
    formData.append("file", file);
    try {
      const { data } = await axios.post("http://localhost:5000/upload", formData);
      setPrediction(data);
    } catch (error) {
      console.error("Error uploading file", error);
      alert("Upload failed.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <Box sx={{ display: "flex" }}>
      <CssBaseline />
      <Drawer
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          "& .MuiDrawer-paper": { width: drawerWidth, boxSizing: "border-box", background: "#2c3e50", color: "white" },
        }}
        variant="permanent"
        anchor="left"
      >
        <Toolbar />
        <List>
          {["Home", "Upload", "Results"].map((text, index) => (
            <ListItem
              button
              key={text}
              onClick={() => {
                if (text === "Home") navigate("/");
                else if (text === "Upload") navigate("/upload");
                else navigate("/results");
              }}
              sx={{ "&:hover": { backgroundColor: "#34495e" } }}
            >
              <ListItemIcon sx={{ color: "white" }}>
                {index === 0 ? <Home /> : index === 1 ? <UploadFile /> : <Image />}
              </ListItemIcon>
              <ListItemText primary={text} />
            </ListItem>
          ))}
        </List>
      </Drawer>

      <Main open={open}>
        <AppBar position="fixed" sx={{ background: "#16a085", ml: drawerWidth }}>
          <Toolbar>
            <Typography variant="h6" sx={{ flexGrow: 1 }}>
              Plant Disease Detection
            </Typography>
            <Avatar src={logo} />
          </Toolbar>
        </AppBar>

        <Box
          sx={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            minHeight: "90vh",
            flexDirection: "column",
            paddingTop: "20px",
            backgroundImage: `url(${bgImage})`,
            backgroundSize: "cover",
            backgroundPosition: "center",
            width: "100%",
          }}
        >
          {!file ? (
            <DropzoneArea acceptedFiles={['image/*']} onChange={handleFileUpload} />
          ) : (
            <Card sx={{ maxWidth: 420, textAlign: "center", padding: 2, borderRadius: 3, boxShadow: 3 }}>
              <CardMedia component="img" height="300" image={preview} />
              <CardContent>
                {loading ? (
                  <CircularProgress />
                ) : prediction ? (
                  <Typography variant="h6" color="primary">
                    {`Detected: ${prediction.class} (${(prediction.confidence * 100).toFixed(2)}%)`}
                  </Typography>
                ) : (
                  <Button
                    variant="contained"
                    sx={{ background: "#e67e22", "&:hover": { background: "#d35400" } }}
                    onClick={uploadImage}
                  >
                    Analyze Image
                  </Button>
                )}
                <Button
                  startIcon={<Delete />}
                  sx={{ marginTop: 2, color: "#c0392b", "&:hover": { color: "#e74c3c" } }}
                  onClick={() => setFile(null)}
                >
                  Remove Image
                </Button>
              </CardContent>
            </Card>
          )}
        </Box>
      </Main>
    </Box>
  );
}

// Export ImageUpload for use in other files
export { ImageUpload };
